<glm_block >{"type": "mcp", "data": {"metadata": {"id": "call_okqh7duqa2r", "name": "Write", "arguments": "{"filepath":"/home/z/my-project/src/app/page.tsx","content":"'use client'\n\nimport { useState } from 'react'\nimport { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'\nimport { Badge } from '@/components/ui/badge'\nimport { Button } from '@/components/ui/button'\nimport { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'\nimport { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from '@/components/ui/accordion'\nimport { Separator } from '@/components/ui/separator'\nimport { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'\nimport { Input } from '@/components/ui/input'\nimport { Search, Filter, Download, Share2, Settings, Eye, Edit, Copy, Maximize2, Minimize2, FileText, Table, Grid3X3, BarChart3 } from 'lucide-react'\n\nexport default function Home() {\n  const [activeView, setActiveView] = useState('tree')\n  const [selectedNode, setSelectedNode] = useState<string | null>(null)\n  const [searchTerm, setSearchTerm] = useState('')\n  const [filterStatus, setFilterStatus] = useState('all')\n  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set(['skylab-root']))\n  const [fullscreen, setFullscreen] = useState(false)\n\n  // Comprehensive Skylab Data Structure\n  const skylabData = {\n    id: 'skylab-root',\n    name: 'Skylab Space Station',\n    type: 'root',\n    status: 'active',\n    description: 'NASA\\'s first space station and the United States\\' first crewed space station',\n    color: 'blue',\n    metadata: {\n      launched: '1973-05-14',\n      missionDuration: '1973-1979',\n      crewCapacity: '3',\n      orbitalAltitude: '435 km',\n      mass: '77,088 kg',\n      volume: '361 m³'\n    },\n    children: [\n      {\n        id: 'structural-systems',\n        name: 'Structural Systems',\n        type: 'category',\n 